import React from 'react';
import ReactDOM from 'react-dom';
import Magician from '../images/Wizard With Long Beard Design-01.png';
import Button from 'react-bootstrap/Button';
import Magiclogo from '../images/Themlogo.png';
import './bubbles.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class SecondDiv extends React.Component{

    state = {bvalue:"TELL"};

    ask = (event) => {
        event.preventDefault();
        this.setState({bvalue:"ASK?"});
        console.log("Clicked");
        console.log(event.target.value);
    };

    render(){
        return(
            <div className="ui fluid container" style={{background: "linear-gradient(90deg, rgba(2,67,111,1) 0%, rgba(78,178,244,1) 35%, rgba(162,214,249,1) 100%)",height: "1200px"}}>
                    <div className="row">
                        <div className="col">
                            <right>
                                COLUMN 1
                                <div className="row align-items-right">
                                <div className="boxl sb13">
                                    Hello! I am The Magician ... Let's start with your name so that i can help you!
                                </div>
                                </div>
                            </right>
                        </div>
                        <div className="col-4">
                            <center>
                                <img src={Magician} alt="Magician Photo" width="600" height="700" style={{marginTop:"100px"}}/>
                                <center><img src={Magiclogo} alt="Magician Logo Photo" width="600" height="250"/></center>
                                <form onSubmit={this.ask} className="ui form" style={{width:"90%" , marginTop:"20px"}}>
                                    <div className="ui container">
                                    <Button variant="outline-primary" size="lg" block type="submit" className="shadow-lg"><h2><b>{this.state.bvalue}</b></h2></Button>
                                    </div>
                                </form>
                            </center>
                        </div>
                        <div className="col">
                            <right>
                                COLUMN 3
                                <div className="boxr sb14">
                                    RIGHT SIDE BOX
                                </div>
                            </right>
                        </div>
                    </div>
            </div>
        );
    }

}

export default SecondDiv;
