import React from 'react';
import ReactDOM from 'react-dom';
import Magician from '../images/Wizard With Long Beard Design-01.png';
import Button from 'react-bootstrap/Button';
import Magiclogo from '../images/logomagic.png';
import './bubbles.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class SecondDiv extends React.Component{

    state = {bvalue:"TELL"};

    ask = (event) => {
        event.preventDefault();
        this.setState({bvalue:"ASK?"});
        console.log("Clicked");
        console.log(event.target.value);
    };

    render(){
        return(
            <div className="ui fluid container" style={{background: "linear-gradient(90deg, rgba(2,67,111,1) 0%, rgba(78,178,244,1) 35%, rgba(162,214,249,1) 100%)",height: "1000px"}}>
                    <div className="row lg-3">
                        <div className="col box">
                            COLUMN 1
                        </div>
                        <div className="col-8 align-items-center">
                            <img src={Magician} alt="Magician Photo" width="600" height="700" style={{marginTop:"100px"}}/>
                            {/* <center><img src={Magiclogo} alt="Magician Logo Photo" width="500" height="600" style={{marginTop:"800px"}}/></center> */}
                            <form onSubmit={this.ask} className="ui form" style={{width:"90%" , marginTop:"20px"}}>
                                <div className="ui container">
                                <Button variant="outline-primary" size="lg" block type="submit" className="shadow-lg"><h2><b>{this.state.bvalue}</b></h2></Button>
                                </div>
                            </form>
                        </div>
                        <div className="col">
                            COLUMN 3
                        </div>
                    </div>
            </div>
        );
    }

}

export default SecondDiv;
