import React from 'react';
import ReactDOM from 'react-dom';
import Magician from '../images/Wizard With Long Beard Design-01.png';
import Button from 'react-bootstrap/Button';
import Magiclogo from '../images/logomagic.png';
import styles from '../components/mystyle.module.css';

const box3 = {
	width: "300px",
	margin: "50px auto",
	borderRadius: "15px",
	background: "#bbeaf3",
	color: "#fff",
	padding: "20px",
	textAlign: "center",
	fontWeight: 900,
	fontFamily: "arial",
    position: "absolute",
    marginTop: "250px"
  };

const sb13:before = {
	content: "",
	width: "0px",
	height: "0px",
	position: "absolute",
	borderLeft: "15px solid #bbeaf3",
	borderRight: "15px solid transparent",
	borderTop: "15px solid #bbeaf3",
	borderBottom: "15px solid transparent",
	right: "-16px",
	top: "0px"
  };



class SecondDiv extends React.Component{

    state = {bvalue:"TELL"};

    ask = (event) => {
        event.preventDefault();
        this.setState({bvalue:"ASK?"});
        console.log("Clicked");
        console.log(event.target.value);
    };

    render(){
        return(
            <div className="ui fluid container" style={{background: "linear-gradient(90deg, rgba(2,67,111,1) 0%, rgba(78,178,244,1) 35%, rgba(162,214,249,1) 100%)",height: "1000px"}}>
                <div style={box3 +' '+ sb13}>Hello</div>
                <center><img src={Magician} alt="Magician Photo" width="500" height="600" style={{marginTop:"100px"}}/></center>
                {/* <center><img src={Magiclogo} alt="Magician Logo Photo" width="500" height="600" style={{marginTop:"800px"}}/></center> */}
                <form onSubmit={this.ask} className="ui form">
                    <div className="ui container" style = {{width: "40%"}}>
                    <center><Button variant="outline-primary" size="lg" block type="submit" className="shadow-lg"><h2><b>{this.state.bvalue}</b></h2></Button></center>
                    </div>
                </form>
            </div>
        );
    }

}

export default SecondDiv;
